import React from 'react'
import { useSelector } from 'react-redux'


const Notification = () => {

	const style = {
		border: 'solid',
		padding: 10,
		borderWidth: 1
	}

	const notification = useSelector(state => state.notification)



	return (

		<div style={notification.style}>
			{notification.notification}
		</div>
	)
}

export default Notification
